import { GET_TOUR_SUCCESS, GET_TOUR_FAIL } from "../types";
import axios from "axios";
import { BASE_URL } from "../../config/config";
import { apis } from "../../config/api";

export const getTour = () => async (dispatch) => {
  try {
    const res = await axios.get(BASE_URL + apis.get_tour);
    dispatch({
      type: GET_TOUR_SUCCESS,
      payload: res.data,
    });
  } catch (error) {
    dispatch({
      type: GET_TOUR_FAIL,
      payload: error.response,
    });
  }


};


export const getTourDetail = (tourId) => async (dispatch) => {
  try {
    const res = await axios.get(BASE_URL + apis.get_tour_detail`/${tourId}`)
    dispatch({
      type: GET_TOUR_DETAIL_SUCCESS,
      payload: res.data
    })
  } catch (error) {
    dispatch({
      type: GET_TOUR_DETAIL_FAIL,
      payload: error.response,
    })
  }
}
