import { GET_TOUR_SUCCESS, GET_TOUR_FAIL } from "../types";
import axios from "axios";

export const getTour = () => async (dispatch) => {
  try {
    const res = await axios.get("/tour");
    dispatch({
      type: GET_TOUR_SUCCESS,
      payload: res.data,
    });
  } catch (error) {
    dispatch({
      type: GET_TOUR_FAIL,
      payload: res,
    });
  }
};
