import React from "react";
import TourCard from "../../shared/TourCard";
//import tourData from "../../assets/data/tours";
import { Col } from "reactstrap";
import { getTour } from "../../redux/action/tour";
import { useDispatch, useSelector } from "react-redux";
import { useEffect } from "react";
import { useState } from "react";

const FeaturedTourList = () => {
  const dispatch = useDispatch();
  const {tour} = useSelector((state)=> state)
  const [tourData, setTourData] = useState([])

  useEffect(() => {
    dispatch(getTour());
  }, []);

  useEffect(()=> {
    if(tour.type === "GET_TOUR_SUCCESS"){
      setTourData(tour.payload.data)
    }
  },[tour])

  return tourData.length ? tourData?.map((tour, i) => (
    <Col lg="3" key={i}>
      <TourCard tour={tour} />
    </Col>
  )):
  <Col lg='12'>
    <h3 className="text-center">No tour found</h3>
  </Col>
};

export default FeaturedTourList;
