import { GET_TOUR_SUCCESS, GET_TOUR_FAIL, GET_TOUR_DETAIL_SUCCESS, GET_TOUR_DETAIL_FAIL } from "../types";

export default function (state = [], action) {
  const { type } = action;
  switch (type) {
    case GET_TOUR_SUCCESS:
      return {
        ...state,
        ...action,
      };
    case GET_TOUR_FAIL:
      return {
        ...state,
      };
      case GET_TOUR_DETAIL_SUCCESS:
        return {
          ...state,
          ...action
        };
        case GET_TOUR_DETAIL_FAIL:
          return {
            ...state,
            ...action
          }
    default:
      return state;
  }
}
