import React, { useEffect } from 'react'
import Header from '../Header/Header'
import Footer from '../Footer/Footer'
import Routers from '../../router/Routers'
import { useDispatch, useSelector } from 'react-redux'
import { getAuthUser } from '../../redux/action/auth'
import { useState } from 'react'

const Layout = () => {
  const dispatch = useDispatch();
  const {auth} = useSelector((state)=>  state)
  const [authData, setAuthData] = useState({})

  useEffect(()=>{
    if(auth.type === "GET_AUTH_USER_SUCCESS"){
      setAuthData(auth.payload)
      // const data = {
      //   token: auth.payload && auth.payload.token,
      //   id: auth.payload && auth.payload.data && auth.payload.data.id
      // };
      // dispatch(getAuthUser(data))
    }

  },[auth])

  useEffect(()=>{
    console.log(authData)
  },[])
  return (
    <>
        <Header/>
        <Routers/>
        <Footer/>
    </>
  )
}

export default Layout
