import React, { useState } from "react";
import "../styles/login.css";
import { Container, Row, Col, Form, FormGroup, Button, Spinner } from "reactstrap";
import { Link } from "react-router-dom";
import loginImg from "../assets/images/login.png";
import userIcon from "../assets/images/user.png";
import { useDispatch, useSelector } from "react-redux";
import { login } from "../redux/action/auth"; 
import { useEffect } from "react";
import { ToastContainer, toast } from "react-toastify";


const Login = () => {
  const dispatch = useDispatch()
  const [loginData, setLoginData] = useState({});
  const {auth} = useSelector((state)=> state)
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    setLoginData({ ...loginData, [e.target.name]: e.target.value });
  };

  const handle_LoginSubmit = (e) => {
    e.preventDefault()
    //console.log(loginData);
    setLoading(false);
    dispatch(login(loginData))
  };

  useEffect(()=> {
    if(auth && auth.payload && auth.payload.data){
      if(auth.type === "USER_LOGIN_SUCCESS"){
        toast(data.message);
        if(data.success){
          window.location.pathname('/home')
        }
      }else{
        const { data } = auth.payload;
        toast(data.message);
      }
    }
    setLoading(false);
  },[auth])

  return (
    <section>
      <Container>
        <Row>
          <Col lg="8" className="m-auto"> 
            <div className="login__container d-flex justify-content-between">
              <div className="login__img">
                <img src={loginImg} alt={loginImg} />
              </div>

              <div className="login__form">
                <div className="login__user">
                  <img src={userIcon} alt="" />
                </div>
                <h2>Login</h2>

                <Form onSubmit={handle_LoginSubmit}>
                  <FormGroup>
                    <input
                      type="email"
                      name="email"
                      placeholder="Email"
                      required
                      onChange={handleChange}
                    />
                  </FormGroup>

                  <FormGroup>
                    <input
                      type="password"
                      name="password"
                      placeholder="Password"
                      required
                      onChange={handleChange}
                    />
                  </FormGroup>

                  {
                    !loading ?
                    <Button className="btn secondary__btn auth_btn" type="submit">
                    Login
                  </Button>
                  :
                  <Button className="btn secondary__btn auth_btn" type="submit">
                   <Spinner color="success" />
                </Button>
                  }

                </Form>
                <p>
                  Don't have an account? <Link to="/register">Create</Link>{" "}
                </p>
              </div>
            </div>
          </Col>
        </Row>
      </Container>
      <ToastContainer />
    </section>
  );
};

export default Login;
