import React from "react";
import "../styles/tour-detail.css";
import { useParams } from "react-router-dom";
import { Container, Row, Col, Form, ListGroup } from "reactstrap";
import tourData from "../assets/data/tours";

const TourDetails = () => {
  const { id } = useParams();
  const tour = tourData.find((tour) => tour.id == id);
  const { photo, title, desc, avgRating, price, reviews, city, distance, maxGroupSize } =
    tour;

    console.log(tour);
  return (
    <section>
      <Container>
        <Row>
          <Col lg="8">
            <div className="tour__content">
              <img src={photo} alt="tour_img" />

              <div className="tour__info">
                <h2> {title} </h2>
                <div className="d-flex align-items-center gap-5">
                <span className="tour__rating d-flex align-items-center gap-1">
                  <i class="ri-star-fill"></i> {avgRating}{" "}
                  <span> {reviews.length} </span>
                </span>

                <span className="d-flex align-items-center gap-1">
                  <i class="ri-map-pin-line"></i> {city}
                </span>
                </div>

                <div className="d-flex align-items-center gap-5">
                <span className="d-flex align-items-center gap-1">
                <i class="ri-group-line"></i> {maxGroupSize} 
                </span>

                <span className="d-flex align-items-center gap-1">
                <i class="ri-money-dollar-circle-line"></i> {price} / per person
                </span>
                </div>
              </div>
            </div>
          </Col>
        </Row>
      </Container>
    </section>
  );
};

export default TourDetails;
