import {USER_LOGIN_SUCCESS, USER_LOGIN_FAIL} from '../types'


export default function(state = {}, action){
    const {type} = action;
    switch (type) {
        case USER_LOGIN_SUCCESS:
            return {
                ...state,
                ...action
            }
        case USER_LOGIN_FAIL:
            return {
                ...state,
            }
        default:
            return state;
    }
}
